mapper.select:
SELECT COUNT(id) FROM `es-java`.`user`

  id=#{id}
  AND username like #{username}
  AND email like #{email}
  AND address like #{address}

ORDER BY id
LIMIT #{pageNumber}, #{pageSize}

SELECT * FROM `es-java`.`user`

  id=#{id}
  AND username like #{username}
  AND email like #{email}
  AND address like #{address}

ORDER BY id
LIMIT #{pageNumber}, #{pageSize}

mapper.insert:

INSERT INTO `es-java`.`user` (id, username, password, email, address, birthday, created_at, created_by, updated_at, updated_by) VALUES (
  #{id}
  ,#{username}
  ,#{password}
  ,#{email}
  ,#{address}
  ,#{birthday}
  ,#{createdAt}
  ,#{createdBy}
  ,#{updatedAt}
  ,#{updatedBy}
)

mapper.update:
UPDATE `es-java`.`user`
username=#{username}, password=#{password}, email=#{email}, address=#{address}, address=#{birthday},
WHERE id = #{id}
